import React, { useState } from 'react';

const CourseRecommendation = () => {
  const [missingSkills, setMissingSkills] = useState('');
  const [courses, setCourses] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);
    
    try {
      const response = await fetch('http://127.0.0.1:5000/recommend_courses', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          missing_skills: missingSkills,
          top_n: 5,
        }),
      });
      
      const data = await response.json();
      
      if (response.ok) {
        setCourses(data.recommended_courses);
      } else {
        setError(data.error);
      }
    } catch (error) {
      setError('Error fetching course recommendations.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2 className="text-2xl font-semibold text-blue-700 mb-4">Course Recommendation</h2>
      <form onSubmit={handleSubmit} className="mb-6">
        <div className="mb-4">
          <label htmlFor="skills" className="block text-gray-700 mb-2">Skills to Learn</label>
          <input
            id="skills"
            type="text"
            placeholder="Enter missing skills"
            value={missingSkills}
            onChange={(e) => setMissingSkills(e.target.value)}
            required
            className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
        </div>
        <button 
          type="submit" 
          className="w-full bg-blue-600 hover:bg-blue-700 text-white font-medium py-2 px-4 rounded-md transition-colors"
          disabled={loading}
        >
          {loading ? 'Searching...' : 'Get Course Recommendations'}
        </button>
      </form>
      
      {error && <p className="p-4 mb-4 bg-red-100 text-red-700 rounded-md">{error}</p>}
      
      {courses.length > 0 && (
        <div>
          <h3 className="text-lg font-medium text-gray-800 mb-2">Recommended Courses</h3>
          <ul className="bg-blue-50 rounded-md p-4">
            {courses.map((course, index) => (
              <li key={index} className="mb-2 p-3 bg-white rounded border-l-4 border-blue-500 shadow-sm">
                {course}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default CourseRecommendation;
