import React, { useState } from "react";

const JobRecommendation = () => {
  const [skills, setSkills] = useState("");
  const [recommendations, setRecommendations] = useState([]);
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    
    try {
      const response = await fetch("http://127.0.0.1:5000/recommend_jobs", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          skills: skills.split(",").map(skill => skill.trim()), // Convert to list
          top_n: 5,
        }),
      });

      const data = await response.json();

      if (response.ok) {
        setRecommendations(data.recommended_jobs);
      } else {
        setError(data.error);
      }
    } catch (error) {
      setError("Error fetching job recommendations.");
    }
  };

  return (
    <div>
      <h2>Job Recommendation</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Enter skills (comma-separated)"
          value={skills}
          onChange={(e) => setSkills(e.target.value)}
          required
        />
        <button type="submit">Get Recommendations</button>
      </form>

      {error && <p style={{ color: "red" }}>{error}</p>}

      {recommendations.length > 0 && (
        <ul>
          {recommendations.map((job, index) => (
            <li key={index}>{job}</li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default JobRecommendation;
