import React from 'react';
import JobRecommendation from './components/jobRecommendation';
import CourseRecommendation from './components/courseRecommendation';

function App() {
  return (
    <div className="min-h-screen bg-blue-50 p-8 font-sans">
      <div className="max-w-4xl mx-auto">
        <header className="text-center mb-12">
          <h1 className="text-4xl font-bold text-blue-800 mb-4">Career Path Recommendation</h1>
          <p className="text-gray-600 max-w-2xl mx-auto">
            Discover job opportunities that match your skills or find courses to enhance your career prospects.
          </p>
        </header>
        
        <div className="grid md:grid-cols-2 gap-8">
          <div className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition-shadow">
            <JobRecommendation />
          </div>
          <div className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition-shadow">
            <CourseRecommendation />
          </div>
        </div>
        
        <footer className="mt-12 text-center text-gray-500 text-sm">
          <p>Â© 2025 Career Path Recommendation System</p>
        </footer>
      </div>
    </div>
  );
}

export default App;
