import React, { useState } from "react";

const CourseRecommendation = () => {
  const [missingSkills, setMissingSkills] = useState("");
  const [courses, setCourses] = useState([]);
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");

    try {
      const response = await fetch("http://127.0.0.1:5000/recommend_courses", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          missing_skills: missingSkills, // Pass as a string
          top_n: 5,
        }),
      });

      const data = await response.json();

      if (response.ok) {
        setCourses(data.recommended_courses);
      } else {
        setError(data.error);
      }
    } catch (error) {
      setError("Error fetching course recommendations.");
    }
  };

  return (
    <div>
      <h2>Course Recommendation</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Enter missing skills"
          value={missingSkills}
          onChange={(e) => setMissingSkills(e.target.value)}
          required
        />
        <button type="submit">Get Courses</button>
      </form>

      {error && <p style={{ color: "red" }}>{error}</p>}

      {courses.length > 0 && (
        <ul>
          {courses.map((course, index) => (
            <li key={index}>{course}</li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default CourseRecommendation;
